import React from "react";
import "./callout.css";
const Callout = ({ attrs, children, editable = false }: any) => {
  const clickIcon = () => {
    if (!editable) return;
    alert("click");
  };
  return (
    <div
      data-text-el="callout"
      className={`callout`}
      style={{
        color: attrs.color,
        background: attrs.background,
        borderColor: attrs.border,
      }}>
      <span className="callout-icon" onClick={clickIcon}>
        {attrs.icon}
      </span>
      {children}
    </div>
  );
};

export default Callout;
